             + em_ibld_Bldgs(3)*ESTMsfr_Bldgs(3) &
             + em_ibld_Bldgs(4)*ESTMsfr_Bldgs(4) &
             + em_ibld_Bldgs(5)*ESTMsfr_Bldgs(5)
        CH_iwall = CH_iwall_Bldgs(1)*ESTMsfr_Bldgs(1) &
             + CH_iwall_Bldgs(2)*ESTMsfr_Bldgs(2) &
