             + CH_ibld_Bldgs(3)*ESTMsfr_Bldgs(3) &
             + CH_ibld_Bldgs(4)*ESTMsfr_Bldgs(4) &
             + CH_ibld_Bldgs(5)*ESTMsfr_Bldgs(5)

     ELSEIF(SurfaceChar(Gridiv,c_ESTMCode(BldgSurf)) /= 0) THEN   !Otherwise use single values
