     !For other surfaces, only one ESTM class
     DO iv=ConifSurf, nsurfIncSnow
        zSurf_SUEWSsurfs(:,iv) = SurfaceChar(Gridiv,(/c_Surf_thick1(iv),c_Surf_thick2(iv),c_Surf_thick3(iv),&
