     HDD_id_grids(:,Gridiv) = HDD_id(:)
     GDD_id_grids(:,Gridiv) = GDD_id(:)
     LAI_id_grids(:,Gridiv) = LAI_id(:)


     ! daily water use
     WUDay_id=0
     WUDay_id_grids(:,Gridiv) = WUDay_id(:)



     ! AlbDecTr_grids(:,Gridiv) = AlbDecTr(:)
     ! AlbEveTr_grids(:,Gridiv) = AlbEveTr(:)
     ! AlbGrass_grids(:,Gridiv) = AlbGrass(:)
