     ! Porosity_grids(:,Gridiv) = Porosity(:)

     ! ---- Snow density of each surface
     SnowDens(1:nsurf) = ModelDailyState(Gridiv,cMDS_SnowDens(1:nsurf))

     ! =============================================================================
